      - Octopus Energy
      - Omie
*/

switch (msg.dynamic.data_source) {
      case "None":
            // Handle the 'None' case (e.g., no supplier selected or data is unavailable)
            log(this,"No data source selected.");
            // Add config here...
            break;

      case "Zonneplan":
            // Code for Zonneplan (a Dutch energy supplier)
            log(this,"Processing Zonneplan data.");
            // Add config here...
            msg.dynamic.sensor='sensor.zonneplan_current_electricity_tariff'; // when using: https://github.com/fsaris/home-assistant-zonneplan-one
            msg.dynamic.attr_all='forecast';
            msg.dynamic.value_key='electricity_price';
            msg.dynamic.value_conversion = 10000000; // whole currency / kWh
            break;

      case "Amber Electric":
            // Code for Amber Electric (Australian energy retailer)
            log(this,"Processing Amber Electric data.");
            // Add config here...
            msg.dynamic.attr_all='forecasts'; 
            msg.dynamic.time_key='start_time'; 
            msg.dynamic.value_key='per_kwh';
            break;

      case "EasyEnergy, EnergyZero (core)":
            // Code for EasyEnergy and EnergyZero (core/default implementation)
            log(this,"Processing EasyEnergy/EnergyZero core data.");
            // Add config here...
            break;

      case "ENTSO-E":
            // Code for ENTSO-E (European Network of Transmission System Operators for Electricity)
            log(this,"Processing ENTSO-E data.");
            // Add config here...
            msg.dynamic.attr_today='prices_today'; 
            msg.dynamic.attr_tomorrow='prices_tomorrow'; 
            msg.dynamic.time_key='time';
            msg.dynamic.value_key='price';
            break;

      case "Frank Energie":
            // Code for Frank Energie (Dutch energy supplier)
            log(this,"Processing Frank Energie data.");
            // Add config here...
            msg.dynamic.attr_all = 'prices'; 
            msg.dynamic.time_key = 'from'; 
            msg.dynamic.value_key = 'price';
            break;

      case "GE-Spot":
            // Code for GE-Spot (Global Electric Spot price)
            log(this,"Processing GE-Spot data.");
            // Add config here...
            msg.dynamic.attr_today = 'today_hourly_prices'; // using the hourly version. today_interval_prices is per 15 mins
            msg.dynamic.attr_tomorrow = 'tomorrow_hourly_prices'; // using the hourly version
            msg.dynamic.time_key = 'time'; 
            msg.dynamic.value_key = 'value';
            break;

      case "Tibber, Nordpool (core)":
            // Code for Tibber and Nordpool (core/default implementation)
            log(this,"Processing Tibber/Nordpool core data.");
            // Add config here...
            msg.dynamic.sensor = 'sensor.tibber_ceh_prices';
            msg.dynamic.attr_today = 'today';
            msg.dynamic.attr_tomorrow = 'tomorrow';
            msg.dynamic.time_key = 'start';
            msg.dynamic.value_key = 'value';
            msg.dynamic.datetime_in_data = true; // datetime is in the data
            break;

      case "Tibber (custom)":
            // Code for a custom Tibber setup/configuration
            log(this,"Processing Tibber custom data.");
            // Add config here...
            msg.dynamic.attr_today = 'today';
            msg.dynamic.attr_tomorrow = 'tomorrow';
            msg.dynamic.datetime_in_data = false;
            break;

      case "Nordpool (custom)":
            // Code for a custom Nordpool setup/configuration
            log(this,"Processing Nordpool custom data.");
            // Add config here...
            break;

      case "PVPC (Spain)":
            // Code for PVPC (Voluntary Price for Small Consumers in Spain)
            log(this,"Processing Spanish PVPC data.");
            // Add config here...
            break;

      case "Octopus Energy":
            // Code for Octopus Energy (UK/international energy supplier)
            log(this,"Processing Octopus Energy data.");
            // Add config here...
            msg.dynamic.attr_all = 'rates'; 
            msg.dynamic.value_key = 'value_incl_vat';
            break;

      case "Omie":
            // Code for Omie (Iberian Energy Market Operator)
            log(this,"Processing Omie data.");
            // Add config here...
            break;

      default:
            // Code to execute if the supplier does not match any of the above cases
            node.warn(`Unknown supplier: ${msg.dynamic.data_source}. Using default logic.`);
            log(this,`Unknown supplier: ${msg.dynamic.data_source}. Using default logic.`);
            // Add default error handling...
}

log(this, "Performing API calls...");

return msg;

// final template initialization
msg.dynamic.template_cheapest = "";
msg.dynamic.template_expensive = "";

// temporary template parts
let template_start = "";
let template_common = [];
let template_cheapest = [];
let template_expensive = [];
let template_all = [];
let template_end = "";

// 1. IMPROVEMENT: Use radix 10 and Logical OR (||) for cleaner default handling
const cheapest_hrs = parseInt(RED.util.getMessageProperty(msg,"dynamic.cheapest_hrs"), 10) || 1;
const expensive_hrs = parseInt(RED.util.getMessageProperty(msg,"dynamic.expensive_hrs"), 10) || 1;

// load the macro
template_start = `{% from 'cheapest_energy_hours.jinja' import cheapest_energy_hours %}
{{ cheapest_energy_hours(
`;
template_end = `) | to_json }}`;

// collect arguments | strings
const stringKeys = [
    'sensor',
    'attr_all',
    'value_key',
    'attr_today',
    'attr_tomorrow',
    'time_key'
];

stringKeys.forEach(key => {
    if (msg.dynamic[key] !== undefined) {
        template_common.push(`${key}='${msg.dynamic[key]}'`);
    }
});

// collect arguments | boolean, integer
const otherKeys = [
    'datetime_in_data',
    'data_minutes',
];

otherKeys.forEach(key => {
    if (msg.dynamic[key] !== undefined) {
        template_common.push(`${key}=${msg.dynamic[key]}`);
    }
});

// copy common items
template_cheapest = [...template_common];
template_expensive = [...template_common];
template_all = [...template_common];

// collect arguments | cheapest specific
template_cheapest.push(`hours=${cheapest_hrs}`);
// template_cheapest.push(`include_tomorrow=true`);
template_cheapest.push(`mode="all"`);

// collect arguments | expensive specific
template_expensive.push(`hours=${expensive_hrs}`);
// template_expensive.push(`include_tomorrow=true`);
template_expensive.push(`lowest=false`);
template_expensive.push(`mode="all"`);

// collect all hours | via extra call
template_all.push(`hours="all"`);
template_all.push(`include_tomorrow=true`);
template_all.push(`mode="all"`);

/**
 * Formats an array of strings into a single string:
 * - Each item starts with 4 spaces.
 * - Each item ends with a comma, except the last one.
 * - Items are separated by a newline.
 */
function formatTemplateArray(array) {
    if (!array || array.length === 0) {
        return "";
    }

    const indent = "    "; 

    const formatted_lines = array.map((element, index) => {
        const suffix = (index < array.length - 1) ? "," : "";
        return `${indent}${element}${suffix}`;
    });

    return formatted_lines.join('\n');
}

// generate string templates
const template_cheapest_string = formatTemplateArray(template_cheapest);
const template_expensive_string = formatTemplateArray(template_expensive);
const template_all_string = formatTemplateArray(template_all);

// done
msg.dynamic.template_cheapest = `${template_start}${template_cheapest_string}${template_end}`;
msg.dynamic.template_expensive = `${template_start}${template_expensive_string}${template_end}`;
msg.dynamic.template_all = `${template_start}${template_all_string}${template_end}`;

return msg;

// Logger
const log = global.get("logger");
log(this,`${msg.target} strategy`);

// Status
node.status({fill:"green",shape:"dot",text:`${msg.target}`});

return msg;

const handle = global.get('unhandledException');

handle(this);

return msg;

// logger, usage: log(this, "");
const log = global.get('logger');

const dynamicData = msg.dynamic;

// Helper to check for errors in the object
const isInvalid = (data) => {
    if (!data) return true;
    // Check if the stringified object contains "error"
    return JSON.stringify(data).toLowerCase().includes("error");
};

if (isInvalid(dynamicData)) {
    // Explain issue
    log(this, "Invalid data received, clearing cache", "warn");
    // Clear cache
    flow.set("cached_dynamic_data", undefined);
} else {
    // Cache on success
    log(this, "Caching `msg.dynamic`");
    // Save the entire object to flow context
    flow.set("cached_dynamic_data", dynamicData);
}

return msg;

const handle = global.get('unhandledException');

handle(this);

return msg;

// Check if cache can be used or a new call to Data Source is required
const log = global.get('logger');

// Retrieve stored data from flow context
const previousValues = flow.get("previous_sensor_values") || {};
const cachedData = flow.get("cached_dynamic_data");

// Create a string or object to represent current state
const currentValues = {
    src: msg.dynamic.data_source,
    hc: msg.dynamic.cheapest_hrs,
    he: msg.dynamic.expensive_hrs
};

// Compare current with previous (JSON stringify is a quick way to compare objects)
const inputsMatch = JSON.stringify(currentValues) === JSON.stringify(previousValues);

// Determine cache hit
if (inputsMatch && cachedData){
    // Cache HIT
    node.status({ fill: "green", shape: "dot", text: `Cache hit` });
    log(this,`*CACHE HIT* using stored tariff data`);

    // Attach cached data to the message so following nodes can use it
    msg.dynamic = cachedData;

    // Output #1
    return [msg, null];

} else {
    // Cache MISS
    node.status({ fill: "green", shape: "ring", text: `Cache miss: ${JSON.stringify(currentValues) }` });

    // Save current values for the next run
    flow.set("previous_sensor_values", currentValues);

    // Output #2
    return [null, msg];
}

// Clear cache
flow.set("cached_dynamic_data", undefined);
return msg;

// logger, usage: log(this, "");
const log = global.get('logger');

// Conversion
const CONVERSION_RATE = parseFloat(RED.util.getMessageProperty(msg, "dynamic.value_conversion")) || 1;

// Check if the list exists and is an array to prevent errors
if (msg.dynamic && msg.dynamic.data_all && Array.isArray(msg.dynamic.data_all.list)) {

    // Create a new array where each value is multiplied
    if (Array.isArray(msg.dynamic.data_all?.list)) {
        msg.dynamic.data_all.list = msg.dynamic.data_all.list.map(v => v / CONVERSION_RATE * 100); // cents
    }

    // Update other fields
    if (msg.dynamic.data_all?.average) {
        msg.dynamic.data_all.average = parseInt(msg.dynamic.data_all.average / CONVERSION_RATE * 100); // cents
    }
} else {
    // Do not continue
    log(this,"*price data not updated* price data missing");
    return null;
}

return msg;
