const log = global.get("logger");
log(this,`full stop`);

// Process
let solution_array = [];

// Add a solution per battery
msg.batteries.forEach(battery => {
    solution_array.push({ id: battery.id, 
    mode: "stop", // disengages Marstek Battery
    power: 0 }); // set power to 0W
});

// OUTPUT
msg.solutions = solution_array;
return msg;
