func:
// Add return node information
let return_to = flow.get("_linkSource");
msg._linkSource = return_to;

// return to calling flow
return msg;

// On change of Ki setting
let logdata = "bumpless ";
let I_sum = flow.get("I_integral_sum");
let Ki = msg.payload || 0;     // Integral gain
let Ki_last = context.get("house_battery_control_ki_last")||0;

// if Ki was or has become zero
if(Ki == 0 || Ki_last == 0){
    // passify the integral-sum
    flow.set("I_integral_sum",0);
    context.set("house_battery_control_ki_last", Ki);
    logdata += `to/from 0, Ki=${Ki_last} -> ${Ki} | `;
    // done
    return { payload: logdata };
}

// change in Ki
let dKi = parseFloat(Ki/Ki_last);
if(dKi <= 0) dKi = 1; // ignore erroneous changes

// keep I-term constant by compensating the integral-sum for the change in Ki
// e.g. if Ki got 10% smaller, then integral-sum should increase 10%, to keep the I-term constant
let I_sum_new = I_sum / dKi;
logdata += `change of I: from ${Ki_last} to ${Ki} (${dKi * 100}%) | `;

// OUTFLOW
flow.set("I_integral_sum", I_sum_new);
context.set("house_battery_control_ki_last", Ki);

// OUTPUT
return { payload: logdata };

initialize:
// Code added here will be run once
// whenever the node is started.

// init last state
let Ki = flow.get("house_battery_control_ki") || 0;
context.set("house_battery_control_ki_last", Ki);

func:
flow.set("LoadDistribution_debug_enabled", msg.payload);
node.warn("Debug flag set to: " + msg.payload);
return msg;

// grid power | power drawn/delivered
let p1_power = Number(msg.grid_power);

// batteries | calculate total and max. power delivered by batteries
let batteries_total_power = 0;
let batteries_max_charging_power = 0;
let batteries_max_discharging_power = 0;
let batteries = msg.batteries;

batteries.forEach(battery => {
    batteries_total_power += Number(battery.power);
    batteries_max_charging_power += Number(battery.charging_max);
    batteries_max_discharging_power += Number(battery.discharging_max);
});

// OUTFLOW
flow.set("batteries", batteries);
flow.set("batteries_total_power", batteries_total_power);
flow.set("batteries_max_charge_power", batteries_max_charging_power);
flow.set("batteries_max_discharge_power", batteries_max_discharging_power);
flow.set("p1_power", p1_power);

return msg;

flow.set("has_soc_charging_limiter", true);         // slows charging from 90% to 100% to improve battery life
flow.set("has_reverse_priority_discharge", true);   // Prioritize discharging and charging the same battery when possible

// Note: batteries get charged in the priority and order provided as input.
return msg;

// INPUTS
let P1_error_last = Number(context.get("p1_error_last"))||0; // last error signal level in W
let dampening = Number(flow.get("house_battery_control_error_signal_dampening")) || 0; // 0% - 100%
dampening = dampening / 100; // to Number

// Process Variable (PV) currently measured grid power, Setpoint (SP) desired grid power, set 0 for NoM
var P1_power = flow.get("p1_power"); // PV: consume is positive, supply to grid is negative
var P1_setpoint = flow.get("house_target_grid_consumption_in_w"); // SP: target value

// Calc error signal
let P1_error_unfiltered = P1_setpoint - P1_power;

// Simple averaging filter
let P1_error_filtered = (1 - dampening) * P1_error_unfiltered + (dampening) * P1_error_last;

// OUTFLOW
flow.set("p1_error", P1_error_filtered); // W (watt), error signal = SP - PV
context.set("p1_error_last", P1_error_filtered);

// OUTPUT
return msg;

var P1_power = flow.get("p1_power")||0;
var P1_last_power = Number(context.get("p1_last_power")) ||0;

// PV derivative
var p1_derivative = P1_power - P1_last_power; // devided by unity seconds

// OUTFLOW
flow.set("p1_derivative", p1_derivative);
context.set("p1_last_power", P1_power);

// OUTPUT
return msg;

var P1_error = flow.get("p1_error")||0;
var P1_last_error = Number(context.get("p1_last_error")) ||0;

// Error derivative
// note: error = -PV
var p1_derivative = -(P1_error - P1_last_error); // devided by unity seconds

// OUTFLOW
flow.set("p1_derivative", p1_derivative);
context.set("p1_last_error", P1_error);

// OUTPUT
return msg;

// Deadband
// Stop processing if error is within .. Watt of target grid consumption.
// This to reduce CPU loads 

let P1_error = flow.get("p1_error"); // Watt
let P1_deadband = 30; // Watt

// Within Deadband
if(Math.abs(P1_error) < P1_deadband) {
    // stop processing
    node.status({fill:"yellow",shape:"dot",text:`Halted, ${Math.round(Math.abs(P1_error))} W`});
    node.log(`Stopped processing | error is within deadband of ${P1_deadband}`);
    return null;
} 

// Outside deadband
node.status({fill:"green",shape:"dot",text:`Passed, ${Math.round(Math.abs(P1_error))} W`});
return msg;

var logdata = "";

// Timing
let time_last = context.get('time_last') || Date.now(); // Milliseconds
let time_current = Date.now(); // Milliseconds
let time_delta = (time_current - time_last) / 1000; // Convert to seconds
// note: due to inherent 1 sec intervals of P1 meter we omit dt terms. This is mostly for bug checking and optimizations.

// Batteries
var batteries = flow.get("batteries"); // array of battery configurations
var B_power = flow.get("batteries_total_power"); // charging is positive, discharging negative
var anti_windup_threshold = Number(flow.get("batteries_total_assignable_power")) || 0; // max available charge/discharge power.

// Process Variable (PV) currently measured grid power, Setpoint (SP) desired grid power, set 0 for NoM
var P1_power = flow.get("p1_power"); // PV: consume is positive, supply to grid is negative
var P1_setpoint = flow.get("house_target_grid_consumption_in_w"); // SP: target value
var P1_error = flow.get("p1_error");  // W (watt), error signal = SP - PV
var P1_derivative = flow.get("p1_derivative"); // Derivative of PV, not the error

// PID values
var Kp = flow.get("house_battery_control_kp") || 0.75;  // Proportional gain
var Ki = flow.get("house_battery_control_ki") || 0;     // Integral gain        Ki = Kp/Ti
var Kd = flow.get("house_battery_control_kd") || 0;     // Derivative gain      Kd = Kp*Td

// helpers
var integral_sum = flow.get('I_integral_sum') || 0;
var Gm = flow.get("master_gain")||1; // gain scheduling

// optimizations
var hysteresis = flow.get("house_battery_control_hysteresis_in_w"); // W (watt)

// -- PID regulator --
// Proportional term
let p_term = Gm * Kp * P1_error;

// Integral term
integral_sum += P1_error; 

// apply anti-windup 
let integral_max = anti_windup_threshold / Ki; // improve: anti-windup should only trigger when exceeding the max controlable range
integral_sum = Math.min(Math.max(integral_sum, -integral_max), integral_max);
let i_term = Ki * integral_sum;

// Differential term
let d_term = Gm * Kd * (-P1_derivative); // omitted '/time_delta'. inherent P1 refresh fequency.

// Total PID output
var PID_output = p_term + i_term + d_term; // W (watt), the control input for the battery packs
logdata += `U(${time_delta}s)[${PID_output}] = P(${Kp})[${p_term}] + I(${Ki})[${i_term}] + D(${Kd})[${d_term}] | `;

// Charging or Discharging states
var B_was_charging = context.get("batteries_charging_last") || false;
var B_is_charging = PID_output > 0 ? true : false;

// Hysteresis mode: prevents excessive switching between (dis)charge mode around the zero point.
// if new PID_output lies within hysteresis, it will not switch. 0 = apply no hysteresis
if (B_is_charging !== B_was_charging && Math.abs(PID_output) < hysteresis){
    // log explain
    logdata += `Hysteresis prevented charge mode switch from ${B_was_charging ? "charging" : "discharging"} to ${B_is_charging ? "charging" : "discharging"} as ${Math.abs(PID_output)}W <= ${hysteresis}(hyst) | `;
    // prevent negative charging or positive discharging values (not allowed)
    PID_output = (PID_output < 0 && B_is_charging) || ((PID_output > 0 && !B_is_charging)) ? 0 : PID_output;
    // maintain previous scenario
    B_is_charging = B_was_charging;

} else {
    // save for next iteration
    context.set("batteries_charging_last", B_is_charging); //boolean
}

// OUTPUT
flow.set("batteries", batteries);
flow.set("p1_error", P1_error);
flow.set("batteries_charging", B_is_charging)
flow.set("I_integral_sum", integral_sum);
context.set("time_last", Date.now());

return [{ payload: Number(PID_output) },
        { payload: Number(P1_error)},
        { payload: parseFloat(p_term)},
        { payload: parseFloat(i_term)},
        { payload: parseFloat(d_term)},
        { payload: B_is_charging},
        { payload: logdata }];

// INPUT
let PID_unfiltered = msg.payload; // W Watt
let PID_last = context.get("PID_last")||0; // W watt
let PID_damp = Number(flow.get("house_battery_control_pid_output_dampening"))||0; // 0% - 100%
PID_damp = PID_damp/100; // to Number

// simple averaging filter
let PID_filtered = (1-PID_damp)*PID_unfiltered + (PID_damp)*PID_last;

// OUTFLOW
context.set("PID_last", PID_filtered);

// OUTPUT
return { payload: Number(PID_filtered)};

// INFLOW
let maxCharge = flow.get("batteries_max_charge_power")||undefined;
let maxDischarge = flow.get("batteries_max_discharge_power")||undefined;
let isCharging = flow.get("batteries_charging")||undefined;
let PID_output = msg.payload; // Watt

// No failsafe
if (maxCharge === undefined || maxDischarge == undefined) {
    node.status({ fill: "red", shape: "dot", text: "disabled" });
    node.warn(`Output Failsafe is INACTIVE. Max charge or discharge limits are undefined`);
    // continue without safeguard
    return msg;
} 

// limit
let limit = 0;

// charging state unknown
if(isCharging === undefined) {
    // limit on lowest of charge / discharge
    limit = Math.min(maxCharge,maxDischarge);    
}
// charging state known
limit = isCharging ? maxCharge : maxDischarge;

// Safe output
if (PID_output <= limit){
    node.status({ fill: "green", shape: "dot", text: "safe" });
    return msg;
} 

// Unsafe, limit output
node.status({ fill: "yellow", shape: "ring", text: "power limiting" });
return {payload: Number(limit)};

//Debug toggle
let debugLog = flow.get("LoadDistribution_debug_enabled") === true;

// INPUT
var batteries = flow.get("batteries"); // Battery configuration as provided by `Home Batteries IO` flow
var isCharging = flow.get("batteries_charging"); // Are we in a battery charging scenario?
var hasChargingLimiter = flow.get("has_soc_charging_limiter") || false; // Battery life improvement
var hasReverseDischargePriority = flow.get("has_reverse_priority_discharge") || false; // Prioritize (dis)charging the same battery
var logdata = `Charging=${isCharging} | `; // logging for node-red debug node

// how much power do the batteries need to compensate?
var unassigned_power = Math.round(Math.abs(msg.payload)); // Power in W to compensate using batteries
logdata += `Unassigned power ${unassigned_power} W | `;

// inits 
var solution_array = []; // load distribution solutions
var batteries_total_assignable_power = 0; // Current max. available total (dis)charge power. Exceeding this max should trigger the anti-windup routine for the Integral terms

// enums
const CMODE = {
    STOP: "stop", // Marstek batteries disconnect a relay when this is set or Power is 0W
    CHARGE: "charge",
    DISCHARGE: "discharge"
}

// debug output function
function debug(message) {
    if (debugLog) {
        node.warn(message);
        node.status({fill:"yellow",shape:"ring",text:"debugging"});
    } else {
        node.status({fill:"green",shape:"dot",text:"operating"});
    }
}
debug(`=== Cycle #${Date.now()} ===`);

// battery life improvement (slow charge near max SoC)
/**
* @param {number} soc
* @param {number} max_power
*/
function chargingLimiter(soc, max_power) {
    if(max_power < 0) max_power = 0;
    if (!hasChargingLimiter) return max_power;
    if (soc >= 98) return Math.min(300, max_power);
    if (soc >= 95) return Math.min(500, max_power);
    if (soc >= 85) return Math.min(1500, max_power);
    return max_power;
}
if (isCharging) logdata += `Charge limiting based on SoC | `;

/**
* battery life improvement (disconnects battery only if the battery is not used for ... seconds)
* @param {string | number} batteryID
* @returns {{id: string|number, mode: string, power: number}} battery solution
*/
function getStopSolution(batteryID) {
    // idle time, before stop is given to inverter relay.
    let time_idle_minimum = flow.get("idle_time_minutes")*60*1000||0; // Milliseconds

    // retrieve last registered active times for each battery based on their ids
    let lasttime_active = context.get("lasttime_active");

    // battery exists in register 
    if(lasttime_active[batteryID] !== undefined){
        // timing
        let time_last = lasttime_active[batteryID]; // Milliseconds 
        let time_now = Date.now();                  // Milliseconds 
        let time_idle = (time_now - time_last); // Milliseconds

        // battery is ready for STOP
        if (time_idle >= time_idle_minimum) return {id: batteryID, mode: CMODE.STOP, power:0}; // STOP or 0 Watt disconnects relay
        // battery is kept IDLE, while awaiting minimum inactive time
        logdata += `Idle: ${batteryID} ${Math.round(time_idle/1000)}s | `;
        return { id: batteryID, mode: isCharging ? CMODE.CHARGE : CMODE.DISCHARGE, power: 1 }; // 1 Watt keeps battery IDLE, keeping relay engaged
    }

    // battery missing in register
    logdata += `'Idle: ${batteryID}' unknown last active time | `;
    debug(`[IDLE] Battery '${batteryID}' unknown last active time`);
    return getActiveSolution(batteryID, 1); // set a last active time and engage idle state
}
logdata += "Idle before stop | ";

/**
* Get battery solution and register a last active time.
* @param {any} batteryID
* @param {number} assigned_power
* @returns {{id: string|number, mode: string, power: number}} battery solution
*/
function getActiveSolution(batteryID, assigned_power){
    // register battery as active
    let lasttime_active = context.get("lasttime_active")||[]; // last registered active times for each battery based on their ids
    lasttime_active[batteryID] = Date.now();
    context.set("lasttime_active", lasttime_active);

    // solution
    return {
        id: batteryID,
        mode: isCharging ? CMODE.CHARGE : CMODE.DISCHARGE,
        power: Math.round(assigned_power)
    };
}

// -- BATTERY DISCHARGE PRIORITY
if (!isCharging && hasReverseDischargePriority) {
   batteries.reverse();
}
// In feite triggert dit maar 1x de reverse charging order. Aan de achterkant wordt alles weer terug gereversed. Dus de accus krijgen dezelfde oplossing (alleen met de IDs een keer verwisseld) ongeacht wat hier gebeurt.
// Grootste raadsel, waar verhielp dit jouw issues of zag je de issues tijdelijk niet. Misschien zien we iets anders over het hoofd.
// let lastChargingState = flow.get("last_isCharging");
// if (lastChargingState !== isCharging) {
//     flow.set("last_isCharging", isCharging);
//     if (!isCharging && hasReverseDischargePriority) {
//         batteries.reverse();
//         debug(`Battery order reversed once for discharge priority`);
//     }
// }

// -- LOAD BALANCER --
batteries.forEach((/** @type {{ id: any; soc: number; soc_min: number; soc_max: number; rs485: string; charging_max: number; discharging_max: any; }} */ battery) => {

    debug(`[CHECK] Battery ${battery.id}: SoC=${battery.soc}%, Min=${battery.soc_min}%, Max=${battery.soc_max}%, RS485=${battery.rs485}, isCharging=${isCharging}`);

    // check if battery is available for (dis)charging
    if ((battery.soc >= battery.soc_max && isCharging) || 
        (battery.soc <= battery.soc_min && !isCharging) || 
        battery.rs485 !== "enable") {

        // battery is NOT available
        debug(`[SKIP] Battery ${battery.id}: skipped due to SoC/RS485 limit`);
        let solution = getStopSolution(battery.id); // get a soft stop solution
        // add solution, do not update last active time.
        solution_array.push(solution);
        return;
    }

    // battery is AVAILABLE, assign power
    let battery_assignable_power = isCharging ? chargingLimiter(battery.soc, battery.charging_max) : battery.discharging_max;
    let assign = Math.min(unassigned_power, battery_assignable_power);

    // select solution
    var solution;
    if (assign <= 0 ) {
        // no power solution
        solution = getStopSolution(battery.id); // a soft stop solution
        assign = 0;
    } else {
        // assigned power solution
        solution = getActiveSolution(battery.id, Math.round(assign));
    }
    solution_array.push(solution);

    // log
    debug(`[ASSIGN] Battery ${solution.id}: allowed to ${solution.mode} with ${solution.power}W`);

    // remaining power to assign
    unassigned_power -= assign;
    batteries_total_assignable_power += Number(battery_assignable_power);
    //node.warn(`[BATTERY] UPDATE: ID=${battery.id}, new_unassigned=${unassigned_power}W, total_assignable=${batteries_total_assignable_power}W`);
});

// battery discharge priority - put solutions back in initial order
if (!isCharging && hasReverseDischargePriority) {
    solution_array.reverse();
}

// OUTFLOW
flow.set("batteries_total_assignable_power", batteries_total_assignable_power);
logdata += `batteries_total_assignable_power ${batteries_total_assignable_power} | `;

// OUTPUT
solution_array.forEach(solution => { 
    logdata += `Solution: ${solution.id}, ${solution.mode} ${solution.power} W | `;
});

// TWO OUTPUTS
return [
    { payload: logdata },    // debug output
    { payload: solution_array }, // to Home Battery flow
    ];

